package ca.uottawa.okorol.bioinf.ModuleInducer.services;

import java.io.File;
import java.util.ArrayList;

import ca.uottawa.okorol.bioinf.ModuleInducer.data.Feature;
import ca.uottawa.okorol.bioinf.ModuleInducer.exceptions.DataFormatException;
import ca.uottawa.okorol.bioinf.ModuleInducer.interfaces.RegulatoryElementService;
import ca.uottawa.okorol.bioinf.ModuleInducer.interfaces.RegulatoryRegionService;
import ca.uottawa.okorol.bioinf.ModuleInducer.properties.SystemVariables;
import ca.uottawa.okorol.bioinf.ModuleInducer.tools.FeaturesTools;

/* Positive example regulatory regions  of this class are hard-coded info from the article  
 * "Identification of muscle-specific regulatory modules in Caenorhabditis elegans" 
 * by Guoyan Zhao, Lawrence A. Schriefer and Gary D. Stormo
 * from http://www.genome.org
 * 
 * Sequence info was manually obtained from ... online database
 */
public class CElegansRegRegionService implements RegulatoryRegionService {
	
	private ArrayList<Feature> positiveRegRegions;
	private ArrayList<Feature> negativeRegRegions;
	
	private static String NEGATIVE_REGION_PREFIX = "negExSeq_";
	
	/*
	 *  Simple simulated sequences
	 */
	public CElegansRegRegionService(int negExMultiplicationFactor) throws DataFormatException{
		positiveRegRegions = this.generateRegulatoryRegions();
		negativeRegRegions = FeaturesTools.generateSimulatedMC1RegulatoryRegions(
					positiveRegRegions, 
					negExMultiplicationFactor, 
					NEGATIVE_REGION_PREFIX);
	}
	
	/*
	 *  Simulated sequences with randomly planted PSSMs
	 */
	public CElegansRegRegionService(int negExMultiplicationFactor, RegulatoryElementService regElService) throws DataFormatException{
		positiveRegRegions = this.generateRegulatoryRegions();
		negativeRegRegions = FeaturesTools.generateSimulatedRegulatoryRegionsWithPSSMs(
				negExMultiplicationFactor, 
				positiveRegRegions, 
				regElService);
	}
	
	/*
	 * Simulated sequences with positionally planted PSSMs
	 */
	public CElegansRegRegionService(int negExMultiplicationFactor, String tempJobDir, File pwmDir) throws DataFormatException{
		positiveRegRegions = this.generateRegulatoryRegions();
		
		int seqLen = positiveRegRegions.get(9).getSequence().length(); //just a random pick of 9
		double[] transitionMatrix = new double[]{0.25, 0.25, 0.25, 0.25};
		
	    negativeRegRegions = FeaturesTools.generateSimulatedRegulatoryRegionsWithPositionalPSSMs(
				positiveRegRegions.size(), 
				seqLen, 
				transitionMatrix, 
				SystemVariables.getInstance().getPositivePatserCutOffScore(),
				"simul_", 
				positiveRegRegions, 
				new PatserRegElementService(pwmDir, tempJobDir)
		);
	}
	
	
	@Override
	public ArrayList<Feature> getPositiveRegulatoryRegions(){
		return positiveRegRegions;
	}
	@Override
	public void setPositiveRegulatoryRegions(ArrayList<Feature> regRegions) {
		positiveRegRegions = regRegions;
	}
	
	@Override
	public ArrayList<Feature> getNegativeRegulatoryRegions(){
		return negativeRegRegions;
	}
	@Override
	public void setNegativeRegulatoryRegions(ArrayList<Feature> regRegions) {
		negativeRegRegions = regRegions;
	}

	private ArrayList<Feature> generateRegulatoryRegions(){
	
		ArrayList<Feature> regRegion = new ArrayList<Feature>();
		
		regRegion.add(new Feature("B0304.1", Feature.TYPE_REGULATORY_REGION, "II", 4519057, 4522425, "", 0.0));

		regRegion.add(new Feature("C02B8.4", Feature.TYPE_REGULATORY_REGION, "X", 8116581, 8120049, "", 0.0));

		regRegion.add(new Feature("C09D1.1a", Feature.TYPE_REGULATORY_REGION, "I", 4035763, 4090962, "", 0.0));

		regRegion.add(new Feature("C36E6.5", Feature.TYPE_REGULATORY_REGION, "X", 17456509, 17455508, "", 0.0));

		regRegion.add(new Feature("F07A5.7", Feature.TYPE_REGULATORY_REGION, "I", 7383194, 7376605, "", 0.0));

		regRegion.add(new Feature("F11C3.3", Feature.TYPE_REGULATORY_REGION, "I", 14863527, 14852479, "", 0.0));

		regRegion.add(new Feature("F29F11.5", Feature.TYPE_REGULATORY_REGION, "V", 10672434, 10675619, "", 0.0));

		regRegion.add(new Feature("F40E10.3", Feature.TYPE_REGULATORY_REGION, "X", 14684796, 14688286, "", 0.0));

		regRegion.add(new Feature("F55B12.1", Feature.TYPE_REGULATORY_REGION, "V", 13813289, 13815252, "", 0.0));

		regRegion.add(new Feature("F58A3.2a", Feature.TYPE_REGULATORY_REGION, "X", 11016336, 11023334, "", 0.0));

		regRegion.add(new Feature("K12F2.1", Feature.TYPE_REGULATORY_REGION, "V", 12236664, 12226794, "", 0.0));

		regRegion.add(new Feature("R06C7.10", Feature.TYPE_REGULATORY_REGION, "I", 7267077, 7258919, "", 0.0));

		regRegion.add(new Feature("T18D3.4", Feature.TYPE_REGULATORY_REGION, "X", 12467223, 12475264, "", 0.0));

		regRegion.add(new Feature("W09B12.1", Feature.TYPE_REGULATORY_REGION, "X", 16367147, 16373955, "", 0.0));

		regRegion.add(new Feature("Y105E8B.1a", Feature.TYPE_REGULATORY_REGION, "I", 14631241, 14620747, "", 0.0));

		regRegion.add(new Feature("Y105E8B.1c", Feature.TYPE_REGULATORY_REGION, "I", 14631241, 14620747, "", 0.0));
		
		return regRegion;
	}


	@Override
	public void updateNumberOfPositiveRegRegions(int num) throws DataFormatException {
		throw new DataFormatException("Can not update the number of positive C. elegans regulatory regions. The regions are hardcoded.");
		
	}

	@Override
	public void updateNumberOfNegativeRegRegions(int multiplicationFactor) throws DataFormatException{
		// TODO perhaps don't do it from scratch, but increase or decrease existing negativeRegRegions
		negativeRegRegions = FeaturesTools.generateSimulatedMC1RegulatoryRegions(
					positiveRegRegions, multiplicationFactor, NEGATIVE_REGION_PREFIX);
	}



}

